mapper.update:
update account
set balance =
balance - #{ money }
where acc_pwd = #{ accPwd } and acc_no = #{
withdrawAccNo }

update account
set balance =
balance + #{ money }
where acc_no = #{ depositAccNo }

mapper.select:
select acc_no, trans_date, trans_class, memo, withdraw, deposit, balance, opp_acc_no
from transaction
where acc_no = #{ accNo } 
		and trans_date between #{ startDate } and #{ endDate }

select sum(withdraw) as withSum, sum(deposit) as depoSum
from transaction
where acc_no = #{ accNo }

select acc_no, category, set_money, start_date, cycle, status
from autodebit
where acc_no = #{ accNo }
